let id=0;export const tile=props=>`\n  <div class="tile">\n    <img data-src="assets/images/${props.src}.png" data-alt="${props.alt}" loading="lazy" onclick="zoom('id_${id}')" id="id_${id++}"/>\n    <div class="modal">\n      <i class="fas fa-times close"></i>\n      <img class="modal-content">\n      <div class="caption"></div>\n    </div>\n  </div>`;window.onscroll=()=>{window.pageYOffset>200&&document.querySelectorAll("img").forEach(img=>{["data-src","data-alt"].forEach(attr=>{img.hasAttribute(attr)&&(img.setAttribute(attr.slice(5),img.getAttribute(attr)),img.removeAttribute(attr))})})};